import { Component, Input } from "@angular/core";

@Component({
  selector: "app-course-info",
  templateUrl: "./course-info.component.html",
  styleUrls: ["./course-info.component.scss"],
})
export class CourseInfoComponent {
  @Input() title!: string;
  @Input() description!: string;
  @Input() id!: string;
  @Input() creationDate!: Date;
  @Input() duration!: number;
  @Input() authors!: string[];

  goBack() {
    // Implement the logic to go back
  }
}
<div class="title">{{ title }}</div>
<div class="course-info">
  <div class="course-info__description">
    {{ description }}
  </div>
  <div class="course-info__info">
    <div>
      <b>ID: </b><span class="course-info__info-id">{{ id }}</span>
    </div>
    <div>
      <b>Duration: </b><span class="course-info__info-duration">{{ duration }} minutes</span>
    </div>
    <div>
      <b>Created: </b><span class="course-info__info-date">{{ creationDate | date }}</span>
    </div>
    <div>
      <b>Authors: </b><span class="course-info__info-authors">{{ authors.join(", ") }}</span>
    </div>
  </div>
</div>
<div class="navigation-block">
  <button (click)="goBack()">Back</button>
</div>
