import { Component } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '@app/auth/services/auth.service';


@Component({
  selector: 'app-registration-form',
  templateUrl: './registration-form.component.html',
  styleUrls: ['./registration-form.component.scss'],
})
export class RegistrationFormComponent {
  registrationForm!: FormGroup;
  submitted = false;

  constructor(private fb: FormBuilder, private authService: AuthService, private router: Router) {
    this.registrationForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(6)]],
      email: ['', [Validators.required]],
      password: ['', [Validators.required]]
    });
  }

  get name() {
    return this.registrationForm.get('name');
  }

  get email() {
    return this.registrationForm.get('email');
  }

  get password() {
    return this.registrationForm.get('password');
  }

  onSubmit(): void {
    this.submitted = true;

    if (this.registrationForm.valid) {
      const user = {name: this.name?.value, email: this.email?.value, password: this.password?.value}
      this.authService.register(user).subscribe(
        response => {
          console.log('Reg form submitted', response)
          this.router.navigate(['/courses'])
        },
        error => {
          console.error('Login failed', error);
        }
      ); 
    } else {
      console.log('Invalid reg');
      this.registrationForm.markAllAsTouched();
    }
  }

  // Use the names `name`, `email`, `password` for the form controls.
}
<h2 class="text-align-center">Registration</h2>
<div class="app-plate">
  <form [formGroup]="registrationForm" (ngSubmit)="onSubmit()">
    <div class="registration-form">
      <div>
        <div class="form__control">
          <label for="name">Name</label>
          <!--Use the name `name` for the form control.-->
          <input type="text" id="name" placeholder="Input text" formControlName="name"/>
          <div *ngIf="registrationForm.get('name')?.touched">
            <span id="nameErrorMessage" class="text-danger" *ngIf="registrationForm.get('name')?.errors?.['required']">Name is required.</span>
          </div>
        </div>
        <div class="form__control">
          <!--Use the name `email` for the form control.-->
          <label for="email">Email</label>
          <input type="text" id="email" name="email" placeholder="Input text" formControlName="email" emailValidator/>
          <div *ngIf="registrationForm.get('email')?.touched">
            <span id="emailErrorMessage" class="text-danger" *ngIf="registrationForm.get('email')?.errors?.['required']">Email is required.</span>
            <span id="emailErrorMessageInvalid" class="text-danger" *ngIf="registrationForm.get('email')?.errors?.['invalidEmail']">Email is not valid.</span>
          </div>
        </div>
        <div class="form__control">
          <!--Use the name `password` for the form control.-->
          <label for="password">Password</label>
          <input type="password" id="password" name="password" placeholder="Input text" formControlName="password"/>
          <div *ngIf="registrationForm.get('password')?.touched">
            <span id="passwordErrorMessage" class="text-danger" *ngIf="registrationForm.get('password')?.errors?.['required']">Password is required.</span>
            <span id="passwordErrorMessageLength" class="text-danger" *ngIf="registrationForm.get('password')?.errors?.['minlength']">Password must be at least 6 characters length.</span>
          </div>
        </div>
      </div>
      <div class="form__action" (click)="onSubmit()">
          <app-button buttonText="Register"></app-button> 
      </div>
      <div class="form__text">
        If you have an account you may <strong><a class="link-strong" routerLink="/login">Login</a></strong>
      </div>
    </div>
  </form>
</div>
