import { Component, Input, Output, EventEmitter } from '@angular/core';
import { Router } from '@angular/router';


@Component({
  selector: 'app-course-card',
  templateUrl: './course-card.component.html',
  styleUrls: ['./course-card.component.scss']
})
export class CourseCardComponent {
  @Input() title!: string;
  @Input() description!: string;
  @Input() creationDate!: Date;
  @Input() duration!: number;
  @Input() authors!: string[];
  @Input() editable: boolean = false;
  @Input() id!: any;

  @Output() clickOnShow = new EventEmitter<void>();

  constructor( private router: Router) {}

  showCourse(): void {
    this.clickOnShow.emit(this.id);
  }

  editCourse() {
    this.router.navigate([`/courses/edit/${this.id}`]);
  }

}
<div class="course-card" >
  <div class="course-card__description">
    <!-- Replace ::text:: with your code -->
    <div class="course-card__description-title">{{ title }}</div>
    <div class="course-card__description-text">{{ description }}</div>
  </div>
  <div class="course-card__info">
      <div class="course-card__info-text">
          <div>
            <b>Authors: </b>
            {{ authors }}
          </div>
          <div><b>Duration: </b><span class="course-card__duration">{{ duration | duration }} hours</span></div>
          <div><b>Created: </b><span class="course-card__date">{{ creationDate | customDate }}</span></div>
      </div>
      <div class="course-card__info-actions">
          <app-button buttonText="Show Course" (click)="showCourse()" routerLink="/courses/:id"></app-button>
          <div class="course-card__buttons" *ngIf="editable">
            <app-button [iconName]="['fas', 'trash-can']"></app-button>
            <app-button [iconName]="['fas', 'pencil']" (click)="editCourse()"></app-button>
          </div>
      </div>
  </div>
</div>
