import { Component, OnInit } from "@angular/core";
import { FormBuilder, FormGroup, Validators } from "@angular/forms";

@Component({
  selector: "app-registration-form",
  templateUrl: "./registration-form.component.html",
  styleUrls: ["./registration-form.component.scss"],
})
export class RegistrationFormComponent implements OnInit {
  registrationForm!: FormGroup;

  constructor(private fb: FormBuilder) {}

  ngOnInit(): void {
    this.registrationForm = this.fb.group({
      name: ["", [Validators.required, Validators.minLength(6)]],
      email: ["", [Validators.required, Validators.email]],
      password: ["", [Validators.required, Validators.minLength(8)]],
    });
  }

  get name() {
    return this.registrationForm.get("name");
  }

  get email() {
    return this.registrationForm.get("email");
  }

  get password() {
    return this.registrationForm.get("password");
  }

  onSubmit() {
    if (this.registrationForm.valid) {
      console.log("Registration form data:", this.registrationForm.value);
    } else {
      this.registrationForm.markAllAsTouched();
    }
  }
}
<h2 class="text-align-center">Registration</h2>
<div class="app-plate">
  <form [formGroup]="registrationForm" (ngSubmit)="onSubmit()" novalidate>
    <div class="registration-form">
      <div class="form__control">
        <label for="name">Name</label>
        <input type="text" id="name" formControlName="name" placeholder="Enter your name" />
        <span *ngIf="name?.invalid && name?.touched" class="text-danger">
          <span *ngIf="name?.errors?.['required']">Name is required.</span>
          <span *ngIf="name?.errors?.['minlength']">Name must be at least 6 characters long.</span>
        </span>
      </div>

      <div class="form__control">
        <label for="email">Email</label>
        <input type="email" id="email" formControlName="email" placeholder="Enter your email" />
        <span *ngIf="email?.invalid && email?.touched" class="text-danger">
          <span *ngIf="email?.errors?.['required']">Email is required.</span>
          <span *ngIf="email?.errors?.['email']">Please enter a valid email address.</span>
        </span>
      </div>

      <div class="form__control">
        <label for="password">Password</label>
        <input type="password" id="password" formControlName="password" placeholder="Enter your password" />
        <span *ngIf="password?.invalid && password?.touched" class="text-danger">
          <span *ngIf="password?.errors?.['required']">Password is required.</span>
          <span *ngIf="password?.errors?.['minlength']">Password must be at least 8 characters long.</span>
        </span>
      </div>

      <div class="form__action">
        <app-button [buttonText]="'Register'"></app-button>
      </div>

      <div class="form__text">
        If you have an account you may
        <a routerLink="/login" class="font-semibold cursor-pointer">Login</a>
      </div>
    </div>
  </form>
</div>
