import { Component } from "@angular/core";
import { FormBuilder, FormGroup, FormArray, Validators, AbstractControl } from "@angular/forms";
import { FaIconLibrary } from "@fortawesome/angular-fontawesome";
import { fas } from "@fortawesome/free-solid-svg-icons";

@Component({
  selector: "app-course-form",
  templateUrl: "./course-form.component.html",
  styleUrls: ["./course-form.component.scss"],
})
export class CourseFormComponent {
  courseForm: FormGroup;

  availableAuthors: { id: number; name: string }[] = [
    { id: 1, name: "Author 1" },
    { id: 2, name: "Author 2" },
  ];

  courseAuthors: { id: number; name: string }[] = [];

  constructor(private fb: FormBuilder, private library: FaIconLibrary) {
    this.library.addIconPacks(fas);
    this.courseForm = this.fb.group({
      title: ["", [Validators.required, Validators.minLength(2)]],
      description: ["", [Validators.required, Validators.minLength(2)]],
      author: ["", [Validators.pattern("^[a-zA-Z0-9 ]*$")]], // Validation for Latin letters/numbers
      authors: this.fb.array([]), // Will use FormArray to handle added authors
      duration: [0, [Validators.required, Validators.min(0)]],
    });
  }

  get title() {
    return this.courseForm.get("title");
  }

  get description() {
    return this.courseForm.get("description");
  }

  get author() {
    return this.courseForm.get("author");
  }

  get authors() {
    return this.courseForm.get("authors") as FormArray;
  }

  get duration() {
    return this.courseForm.get("duration");
  }

  addAuthor(author: any) {
    this.courseAuthors.push(author);
    this.availableAuthors = this.availableAuthors.filter((a) => a.id !== author.id);
    this.authors.push(this.fb.control(author));
  }

  removeAuthor(author: any) {
    this.availableAuthors.push(author);
    this.courseAuthors = this.courseAuthors.filter((a) => a.id !== author.id);
    const index = this.authors.controls.findIndex((control) => control.value.id === author.id);
    this.authors.removeAt(index);
  }

  createAuthor() {
    if (this.author?.valid) {
      const newAuthor = {
        id: Math.floor(Math.random() * 1000),
        name: this.author?.value,
      };
      this.availableAuthors.push(newAuthor);
      this.author?.reset();
    }
  }

  isControlInvalid(control: AbstractControl | null): boolean {
    return !!(control && control.invalid && (control.touched || control.dirty));
  }

  onSubmit() {
    if (this.courseForm.valid) {
      console.log("Form Submitted:", this.courseForm.value);
    } else {
      console.error("Form is invalid");
    }
  }

  formatDuration(minutes: number): string {
    if (minutes <= 0) return "0m";
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours > 0 ? hours + "h " : ""}${mins > 0 ? mins + "m" : ""}`;
  }
}
<h2>Create / Edit Course</h2>
<form [formGroup]="courseForm" (ngSubmit)="onSubmit()">
  <div class="app-plate">
    <div class="course">
      <h3>Main Info</h3>
      <div class="form__control">
        <label for="title">Title</label>
        <input formControlName="title" type="text" id="title" placeholder="Input text" />
        <span *ngIf="isControlInvalid(title)" class="text-danger">Title is required (min 2 characters).</span>
      </div>
      <div class="form__control">
        <label for="description">Description</label>
        <textarea formControlName="description" id="description" rows="4" cols="5" placeholder="Input text"></textarea>
        <span *ngIf="isControlInvalid(description)" class="text-danger"
          >Description is required (min 2 characters).</span
        >
      </div>
      <div class="separator"></div>

      <h3>Authors</h3>
      <div class="form__control">
        <div class="course__flex">
          <input formControlName="author" type="text" id="author" placeholder="Input Author Name" />
          <button type="button" id="createAuthor" (click)="createAuthor()">Create Author</button>
        </div>
        <span *ngIf="isControlInvalid(author)" class="text-danger"
          >New author should contain only latin letters and numbers.</span
        >
        <div class="course__authors">
          <h4>Available Authors</h4>
          <ul>
            <li *ngFor="let author of availableAuthors">
              {{ author.name }}
              <button type="button" (click)="addAuthor(author)">Add</button>
            </li>
          </ul>

          <h4>Course Authors</h4>
          <ul>
            <li *ngFor="let courseAuthor of courseAuthors">
              {{ courseAuthor.name }}
              <button type="button" (click)="removeAuthor(courseAuthor)">Delete</button>
            </li>
          </ul>
        </div>
      </div>
      <div class="separator"></div>

      <h3>Duration</h3>
      <div class="form__control">
        <div class="course__flex">
          <input formControlName="duration" type="number" id="duration" placeholder="Input duration" />
          <div class="course__duration">
            {{ duration?.value | durationPipe }}
            <!-- Assuming a custom duration pipe, otherwise remove -->
          </div>
        </div>
        <span *ngIf="isControlInvalid(duration)" class="text-danger">Duration must be at least 0.</span>
      </div>
      <div class="form__action">
        <button type="submit">Submit</button>
      </div>
    </div>
  </div>
</form>
