  styleUrls: "./list-items.component.css",
})
export class ListItemsComponent {
  items: Item[] = [
    { id: 1, name: "item1" },
    { id: 2, name: "item2" },
    { id: 3, name: "item3" },
  ];
  selectedItem: Item;
