import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Course } from '@app/models/course.model';
import { CoursesStateFacade } from '@app/store/courses/courses.facade';


@Component({
  selector: 'app-course-info',
  templateUrl: './course-info.component.html',
  styleUrls: ['./course-info.component.scss']
})
export class CourseInfoComponent implements OnInit {

  course: Course = {
    title: '',
    description: '',
    id: '',
    creationDate: '',
    duration: 0,
    authors: []
  };

  constructor (private coursesFacade: CoursesStateFacade, private route: ActivatedRoute) {}

  ngOnInit(): void {
    const courseId = this.route.snapshot.paramMap.get('id');
    console.log(courseId)

    if (courseId){
      this.coursesFacade.getSingleCourse(courseId);

      this.coursesFacade.course$.subscribe(
        (course) => {
          if (course) {
            this.course = course
          }
        },
        (error) => {
          console.log('Error fetching course data:', error);
        }
      );
    } else {
      console.error('No course ID found in this route.')
    }
  }
  // Use the names for the input `course`.
}
<div class="title">{{ course.title || 'No title available' }}</div>
<div class="course-info">
  <div class="course-info__description">
    {{ course.description || 'No description available' }}
  </div>
  <div class="course-info__info">
    <div><b>ID: </b><span class="course-info__info-id">{{ course.id || 'N/A' }}</span></div>
    <div><b>Duration: </b>
      <span class="course-info__info-duration">
        {{ course.duration !== undefined && course.duration !== null ? (course.duration | duration) : 'N/A' }}
      </span>
    </div>
    <div><b>Created: </b>
      <span class="course-info__info-date">
        {{ course.creationDate ? (course.creationDate | customDate) : 'N/A' }}
      </span>
    </div>
    <div><b>Authors: </b>
      <span class="course-info__info-authors">
        {{ course.authors && course.authors.length > 0 ? course.authors.join(', ') : 'Unknown' }}
      </span>
    </div>
  </div>
</div>
<div class="navigation-block">
  <app-button buttonText="Back" routerLink="/courses"></app-button>
</div>
