import { Component, Input, Output, EventEmitter } from "@angular/core";

@Component({
  selector: "app-course-card",
  templateUrl: "./course-card.component.html",
  styleUrls: ["./course-card.component.scss"],
})
export class CourseCardComponent {
  @Input() title!: string;
  @Input() description!: string;
  @Input() creationDate!: string; // Adjusted for simplicity
  @Input() duration!: number;
  @Input() authors!: string[];
  @Input() editable: boolean = false; // Determines if course is editable

  @Output() clickOnShow = new EventEmitter<void>(); // Event emitter for show course action

  showCourse() {
    this.clickOnShow.emit();
  }
}
<div class="course-card">
  <div class="course-card__description">
    <div class="course-card__description-title">{{ title }}</div>
    <div class="course-card__description-text">{{ description }}</div>
  </div>
  <div class="course-card__info">
    <div class="course-card__info-text">
      <div><b>Authors: </b>{{ authors.join(", ") }}</div>
      <div>
        <b>Duration: </b><span class="course-card__duration">{{ duration }} hours</span>
      </div>
      <div>
        <b>Created: </b><span class="course-card__date">{{ creationDate }}</span>
      </div>
    </div>
    <div class="course-card__info-actions">
      <app-button (click)="showCourse()" [buttonText]="'Show Course'" [iconName]="'eye'"></app-button>
      <div class="course-card__buttons" *ngIf="editable">
        <app-button [buttonText]="'Edit'" [iconName]="'edit'"></app-button>
        <app-button [buttonText]="'Remove'" [iconName]="'trash'"></app-button>
      </div>
    </div>
  </div>
</div>
